const sections=document.querySelectorAll("section[id]");function navHighlighter(){let e=window.pageYOffset;sections.forEach((t=>{const i=t.offsetHeight,o=t.offsetTop-50;sectionId=t.getAttribute("id"),e>o&&e<=o+i?document.querySelector(".navigation a[href*="+sectionId+"]").classList.add("active"):document.querySelector(".navigation a[href*="+sectionId+"]").classList.remove("active")}))}window.addEventListener("scroll",navHighlighter);